function trataMensagens(e){$(".message").hide();$(".enviomsg").css("display","none");var t=$("#slides").find(".fotogrande");var n=t.length;if(e==n){e=0}var r=" (Foto "+(e+1)+" de "+$(".totalfotos").attr("data-totalfotos")+")";var i=$(t[e]).attr("data-legenda");var s=$("#list-foto").find("h1").html();var o=$(t[e]).attr("data-fotoid");var u=$(".fotoid");var a=u.val(o);if(i!="null"&&i!=""){$(".caption").html(i+r)}else{$(".caption").html(r)}var f=$("#comentarios");$.ajax({type:"POST",url:"/fotolog/mensagem/mensagemporfoto",data:{idfoto:o},dataType:"json",before:function(){f.innerHTML=""},success:function(e,t,n){var r="<div class='resultados'>";var i=0;while(i<e.mensagens.length){var s=new Date(e.mensagens[i].dateCreated);var o=s.getDate()+"/"+(s.getMonth()+1)+"/"+s.getFullYear();var u=s.getHours()+":"+s.getMinutes();var a="<h1>Em "+o+" as "+u+" "+e.mensagens[i].remetente+" escreveu:</h1>";var l="<p class='mensagem'>"+e.mensagens[i].mensagem+"</p><br>";var c=a+l;r=r+c;i++}r=r+"</div>";f.html(r)},error:function(e,t,n){}})}